import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import apiClient from '../../api/axiosConfig';
import { Container, Button, Table, Spinner, ButtonGroup, Row, Col, Card, Alert } from 'react-bootstrap';

// Componente reutilizável para os cartões de estatísticas
const StatCard = ({ title, value, icon, color }) => (
  <Card className={`shadow-sm border-start border-5 border-${color} h-100`}>
    <Card.Body>
      <Row className="align-items-center">
        <Col>
          <div className={`text-xs fw-bold text-${color} text-uppercase mb-1`}>{title}</div>
          <div className="h5 mb-0 fw-bold text-gray-800">{value}</div>
        </Col>
        <Col xs="auto">
          <i className={`bi ${icon} fs-2 text-muted`}></i>
        </Col>
      </Row>
    </Card.Body>
  </Card>
);

const PainelAdminDashboard = () => {
  const [veiculos, setVeiculos] = useState([]);
  const [stats, setStats] = useState({ totalVeiculos: 0, totalLojas: 0, totalVisualizacoes: 0 });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null); // Novo estado para mensagens de erro
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null); // Limpa erros anteriores

      try {
        // --- INÍCIO DA CORREÇÃO ---
        // 1. Busca os veículos primeiro. Esta é a nossa prioridade.
        const veiculosRes = await apiClient.get('/veiculos');
        setVeiculos(veiculosRes.data);
        console.log("[DEBUG] Veículos recebidos:", veiculosRes.data); // Log para depuração

        // 2. Busca as estatísticas separadamente. Se isto falhar, não impede a tabela de renderizar.
        try {
          const statsRes = await apiClient.get('/dashboard/stats');
          setStats(statsRes.data);
        } catch (statsError) {
          console.error("Aviso: Falha ao buscar estatísticas.", statsError);
          // Podemos definir um erro específico para as estatísticas se quisermos
        }
        
      } catch (mainError) {
        console.error("Erro crítico ao buscar dados do dashboard:", mainError);
        // Se a busca principal (de veículos) falhar, exibe uma mensagem de erro clara.
        setError("Não foi possível carregar os dados dos veículos. Verifique a sua conexão ou tente novamente.");
        if (mainError.response && mainError.response.status === 401) {
          handleLogout();
        }
      } finally {
        setLoading(false);
      }
      // --- FIM DA CORREÇÃO ---
    };
    fetchData();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    navigate('/admin/login');
  };

  const handleDelete = async (id) => {
    if (window.confirm('Tem a certeza de que deseja apagar este veículo?')) {
        try {
            await apiClient.delete(`/veiculos/${id}`);
            setVeiculos(veiculos.filter(v => v._id !== id));
            alert('Veículo apagado com sucesso!');
        } catch (error) {
            console.error("Erro ao apagar veículo:", error);
            alert('Falha ao apagar o veículo.');
        }
    }
  };

  if (loading) {
    return <div className="text-center p-5"><Spinner animation="border" variant="primary" /></div>;
  }

  return (
    <>
      {/* Exibe um alerta de erro se a busca principal falhar */}
      {error && <Alert variant="danger">{error}</Alert>}

      <Row className="mb-4">
        <Col xl={4} md={6} className="mb-4"><StatCard title="Total de Veículos" value={stats.totalVeiculos} icon="bi-car-front-fill" color="primary" /></Col>
        <Col xl={4} md={6} className="mb-4"><StatCard title="Total de Lojas" value={stats.totalLojas} icon="bi-shop" color="success" /></Col>
        <Col xl={4} md={6} className="mb-4"><StatCard title="Total de Visualizações" value={stats.totalVisualizacoes.toLocaleString('pt-BR')} icon="bi-eye-fill" color="warning" /></Col>
      </Row>

      <Card className="shadow-sm">
        <Card.Header as="h4" className="d-flex justify-content-between align-items-center">
          Veículos Recentes
          <Button as={Link} to="/admin/veiculo/novo" variant="primary"><i className="bi bi-plus-circle me-2"></i>Adicionar Veículo</Button>
        </Card.Header>
        <Card.Body>
          <Table striped bordered hover responsive="sm">
            <thead className="table-dark">
              <tr>
                <th>Origem / Título</th>
                <th className="text-center">Tipo</th>
                <th>Marca/Modelo</th>
                <th className="text-center">Visualizações</th>
                <th className="text-center">Ações</th>
              </tr>
            </thead>
            <tbody>
              {veiculos.length > 0 ? (
                veiculos.map(veiculo => (
                  <tr key={veiculo._id}>
                    <td className="align-middle fw-bold">
                      {veiculo.tipoVendedor === 'loja' && veiculo.idVendedor ? (<span className="text-primary">{veiculo.idVendedor.nome}</span>) : (veiculo.titulo)}
                    </td>
                    <td className="text-center align-middle text-capitalize">{veiculo.tipoVendedor}</td>
                    <td className="align-middle">{`${veiculo.detalhes.marca} ${veiculo.detalhes.modelo}`}</td>
                    <td className="text-center align-middle">{veiculo.visualizacoes}</td>
                    <td className="text-center align-middle">
                      <ButtonGroup>
                        <Button as={Link} to={`/admin/veiculo/editar/${veiculo._id}`} variant="outline-warning" size="sm">Editar</Button>
                        <Button variant="outline-danger" size="sm" onClick={() => handleDelete(veiculo._id)}>Apagar</Button>
                      </ButtonGroup>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="5" className="text-center">Nenhum veículo encontrado.</td>
                </tr>
              )}
            </tbody>
          </Table>
        </Card.Body>
      </Card>
    </>
  );
};

export default PainelAdminDashboard;
