import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import apiClient from '../../api/axiosConfig';
import { Container, Form, Button, Card, Row, Col, Spinner, Image, Alert } from 'react-bootstrap';

const FormularioVeiculo = () => {

    const { id } = useParams();
    const navigate = useNavigate();
    const isEditing = Boolean(id);

    const [veiculo, setVeiculo] = useState({
        titulo: '',
        descricao: '',
        fotos: [],
        tipoVendedor: 'loja', // Valor inicial padrão
        idVendedor: '',
        contatoWhatsApp: '',
        localizacao: { cidade: '', estado: 'SP' },
        detalhes: {
            marca: '', modelo: '', ano: '', quilometragem: '', potenciaMotor: '',
            combustivel: 'Flex', cambio: 'Manual', direcao: 'Elétrica',
            portas: 4, finalPlaca: '', categoria: 'Hatch',
            tipoVeiculo: 'Carro', possuiKitGnv: false
        },
    });

    const [lojas, setLojas] = useState([]);
    const [imageFiles, setImageFiles] = useState([]);
    const [imagePreviews, setImagePreviews] = useState([]);
    const [isUploading, setIsUploading] = useState(false);

    // useEffect para buscar dados necessários (veículo para edição e lista de lojas)
    useEffect(() => {
        const fetchLojas = async () => {
            try {
                const response = await apiClient.get('/lojas');
                setLojas(response.data);
                // Se não estiver a editar, define a primeira loja como padrão
                if (!isEditing && response.data.length > 0) {
                    setVeiculo(prev => ({ ...prev, idVendedor: response.data[0]._id }));
                }
            } catch (error) {
                console.error("Erro ao buscar lojas:", error);
            }
        };

        fetchLojas();

        if (isEditing) {
            apiClient.get(`/veiculos/${id}`).then(response => {
                setVeiculo(response.data);
                if (response.data.fotos) {
                    setImagePreviews(response.data.fotos);
                }
            }).catch(err => console.error("Erro ao buscar veículo:", err));
        }
    }, [id, isEditing]);

    const handleImageChange = (e) => {
        const files = Array.from(e.target.files);
        if (imagePreviews.length + files.length > 5) {
            alert("Você pode enviar no máximo 5 fotos.");
            return;
        }
        setImageFiles(prev => [...prev, ...files]);
        const newPreviews = files.map(file => URL.createObjectURL(file));
        setImagePreviews(prev => [...prev, ...newPreviews]);
    };

    const handleRemoveImage = (indexToRemove) => {
        const urlToRemove = imagePreviews[indexToRemove];
        setImagePreviews(prev => prev.filter((_, index) => index !== indexToRemove));
        if (typeof urlToRemove === 'string') {
            setVeiculo(prev => ({
                ...prev,
                fotos: prev.fotos.filter(url => url !== urlToRemove)
            }));
        } else {
            // Lógica para remover o ficheiro do state 'imageFiles' - simplificada
            // Uma abordagem robusta mapearia o URL.createObjectURL de volta ao ficheiro
        }
    };

    const uploadImages = async () => {
        if (imageFiles.length === 0) return [];
        setIsUploading(true);
        const uploadedImageUrls = [];
        const cloudName = process.env.REACT_APP_CLOUDINARY_CLOUD_NAME;
        const uploadPreset = process.env.REACT_APP_CLOUDINARY_UPLOAD_PRESET;
        if (!cloudName || !uploadPreset) {
            throw new Error("Configuração de upload incompleta.");
        }
        const uploadUrl = `https://api.cloudinary.com/v1_1/${cloudName}/image/upload`;
        for (const file of imageFiles) {
            const formData = new FormData();
            formData.append('file', file);
            formData.append('upload_preset', uploadPreset);
            try {
                const response = await fetch(uploadUrl, { method: 'POST', body: formData });
                const data = await response.json();
                if (data.error) throw new Error(`Falha no upload: ${data.error.message}`);
                uploadedImageUrls.push(data.secure_url);
            } catch (error) {
                setIsUploading(false);
                throw error;
            }
        }
        setIsUploading(false);
        return uploadedImageUrls;
    };

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        const [section, field] = name.split('.');
        if (section && field) {
            setVeiculo(prev => ({ ...prev, [section]: { ...prev[section], [field]: value } }));
        } else {
            setVeiculo(prev => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const newImageUrls = await uploadImages();
            const finalIdVendedor = veiculo.tipoVendedor === 'loja' ? veiculo.idVendedor : null;
            const payload = {
                ...veiculo,
                idVendedor: finalIdVendedor,
                fotos: [...(veiculo.fotos || []), ...newImageUrls]
            };
            if (isEditing) {
                await apiClient.put(`/veiculos/${id}`, payload);
                alert('Veículo atualizado com sucesso!');
            } else {
                await apiClient.post('/veiculos', payload);
                alert('Veículo criado com sucesso!');
            }
            navigate('/admin/dashboard');
        } catch (error) {
            console.error("Erro ao salvar veículo:", error);
            const errorMessage = error.response?.data?.message || error.message || "Falha ao salvar o veículo.";
            alert(`Erro: ${errorMessage}`);
        }
    };

        return (
        <Container className="my-4">
            <Card className="shadow-sm border-0">
                <Card.Header as="h2" className="p-3">{isEditing ? 'Editar Veículo' : 'Adicionar Novo Veículo'}</Card.Header>
                <Card.Body className="p-4">
                    <Form onSubmit={handleSubmit}>
                        <Card className="mb-4">
                            <Card.Header>Fotos</Card.Header>
                            <Card.Body>
                                <Form.Group controlId="formFileMultiple">
                                    <Form.Label>Carregar até 5 imagens</Form.Label>
                                    <Form.Control type="file" multiple accept="image/*" onChange={handleImageChange} disabled={imagePreviews.length >= 5} />
                                </Form.Group>
                                <div className="d-flex flex-wrap gap-2 mt-3">
                                    {imagePreviews.map((preview, index) => (
                                        <div key={index} className="position-relative">
                                            <Image src={preview} thumbnail style={{ width: '120px', height: '90px', objectFit: 'cover' }} />
                                            <Button variant="danger" size="sm" className="position-absolute top-0 end-0 m-1" onClick={() => handleRemoveImage(index)}>×</Button>
                                        </div>
                                    ))}
                                </div>
                            </Card.Body>
                        </Card>

                        <Card className="mb-4">
                           <Card.Header>Dados Gerais</Card.Header>
                           <Card.Body>
                                <Row>
                                    <Form.Group as={Col} md={6} className="mb-3"><Form.Label>Título *</Form.Label><Form.Control name="titulo" value={veiculo.titulo} onChange={handleChange} required /></Form.Group>
                                    <Form.Group as={Col} md={6} className="mb-3"><Form.Label>WhatsApp *</Form.Label><Form.Control name="contatoWhatsApp" value={veiculo.contatoWhatsApp} onChange={handleChange} required /></Form.Group>
                                    <Form.Group as={Col} md={6} className="mb-3"><Form.Label>Tipo de Vendedor</Form.Label><Form.Select name="tipoVendedor" value={veiculo.tipoVendedor} onChange={handleChange}>{/* ... options ... */}</Form.Select></Form.Group>
                                    {veiculo.tipoVendedor === 'loja' && <Form.Group as={Col} md={6} className="mb-3"><Form.Label>Loja</Form.Label><Form.Select name="idVendedor" value={veiculo.idVendedor} onChange={handleChange}>{/* ... options lojas ... */}</Form.Select></Form.Group>}
                                </Row>
                                <Form.Group><Form.Label>Descrição *</Form.Label><Form.Control as="textarea" rows={4} name="descricao" value={veiculo.descricao} onChange={handleChange} required /></Form.Group>
                           </Card.Body>
                        </Card>
                        
                        <Card className="mb-4">
                            <Card.Header>Localização</Card.Header>
                            <Card.Body>
                                <Row>
                                    <Form.Group as={Col} md={6}><Form.Label>Cidade *</Form.Label><Form.Control name="localizacao.cidade" value={veiculo.localizacao.cidade} onChange={handleChange} required /></Form.Group>
                                    <Form.Group as={Col} md={6}><Form.Label>Estado *</Form.Label><Form.Select name="localizacao.estado" value={veiculo.localizacao.estado} onChange={handleChange}>{/* ... options estados ... */}</Form.Select></Form.Group>
                                </Row>
                            </Card.Body>
                        </Card>
                        
                        <Card className="mb-4">
                            <Card.Header>Detalhes Técnicos</Card.Header>
                            <Card.Body>
                                <Row>
                                    <Form.Group as={Col} md={4} className="mb-3"><Form.Label>Marca *</Form.Label><Form.Control name="detalhes.marca" value={veiculo.detalhes.marca} onChange={handleChange} required /></Form.Group>
                                    <Form.Group as={Col} md={4} className="mb-3"><Form.Label>Modelo *</Form.Label><Form.Control name="detalhes.modelo" value={veiculo.detalhes.modelo} onChange={handleChange} required /></Form.Group>
                                    <Form.Group as={Col} md={4} className="mb-3"><Form.Label>Ano *</Form.Label><Form.Control type="number" name="detalhes.ano" value={veiculo.detalhes.ano} onChange={handleChange} required /></Form.Group>
                                    {/* ... outros campos de detalhes ... */}
                                </Row>
                            </Card.Body>
                        </Card>

                        <div className="text-end mt-4">
                            <Button variant="secondary" onClick={() => navigate('/admin/dashboard')} className="me-2">Cancelar</Button>
                            <Button variant="primary" type="submit" disabled={isUploading}>
                                {isUploading ? <><Spinner as="span" animation="border" size="sm" /> A enviar...</> : (isEditing ? 'Salvar Alterações' : 'Criar Veículo')}
                            </Button>
                        </div>
                    </Form>
                </Card.Body>
            </Card>
        </Container>
    );
};
export default FormularioVeiculo;
