import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import apiClient from '../../api/axiosConfig';
import { Container, Button, Table, Spinner, ButtonGroup } from 'react-bootstrap';

const PainelAdminDashboard = () => {
  const [veiculos, setVeiculos] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const fetchVeiculos = async () => {
    try {
      setLoading(true);
      const response = await apiClient.get('/veiculos');
      setVeiculos(response.data);
    } catch (error) {
      console.error("Erro ao buscar veículos:", error);
      if (error.response && error.response.status === 401) {
        handleLogout();
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchVeiculos();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    navigate('/admin/login');
  };

  const handleDelete = async (id) => {
    if (window.confirm('Tem a certeza de que deseja apagar este veículo?')) {
        try {
            await apiClient.delete(`/veiculos/${id}`);
            setVeiculos(veiculos.filter(v => v._id !== id));
            alert('Veículo apagado com sucesso!');
        } catch (error) {
            console.error("Erro ao apagar veículo:", error);
            alert('Falha ao apagar o veículo.');
        }
    }
  };

  if (loading) {
    return <Container className="text-center p-5"><Spinner animation="border" /></Container>;
  }

  return (
    <Container fluid className="mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4 flex-wrap">
        <h1 className="mb-2 mb-md-0">Painel de Controlo de Veículos</h1>
        <div>
          <Button as={Link} to="/admin/lojas" variant="outline-secondary" className="me-2">Gerir Lojas</Button>
          <Button as={Link} to="/admin/veiculo/novo" variant="primary" className="me-2">Adicionar Novo Veículo</Button>
          <Button onClick={handleLogout} variant="danger">Sair</Button>
        </div>
      </div>
      <Table striped bordered hover responsive="sm">
        <thead className="table-dark">
          <tr>
            <th>Origem / Título</th>
            <th className="text-center">Tipo</th>
            <th>Marca/Modelo</th>
            <th className="text-center">Visualizações</th>
            <th className="text-center">Ações</th>
          </tr>
        </thead>
        <tbody>
          {veiculos.length > 0 ? (
            veiculos.map(veiculo => (
              <tr key={veiculo._id}>
                <td className="align-middle fw-bold">
                  {/* --- INÍCIO DA ALTERAÇÃO --- */}
                  {/* Se for de loja, mostra o nome da loja. Se for particular, mostra o título. */}
                  {veiculo.tipoVendedor === 'loja' && veiculo.idVendedor ? (
                    <span className="text-primary">{veiculo.idVendedor.nome}</span>
                  ) : (
                    veiculo.titulo
                  )}
                  {/* --- FIM DA ALTERAÇÃO --- */}
                </td>
                <td className="text-center align-middle text-capitalize">{veiculo.tipoVendedor}</td>
                <td className="align-middle">{`${veiculo.detalhes.marca} ${veiculo.detalhes.modelo}`}</td>
                <td className="text-center align-middle">{veiculo.visualizacoes}</td>
                <td className="text-center align-middle">
                  <ButtonGroup>
                    <Button as={Link} to={`/admin/veiculo/editar/${veiculo._id}`} variant="outline-warning" size="sm">Editar</Button>
                    <Button variant="outline-danger" size="sm" onClick={() => handleDelete(veiculo._id)}>Apagar</Button>
                  </ButtonGroup>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5" className="text-center">Nenhum veículo encontrado.</td>
            </tr>
          )}
        </tbody>
      </Table>
    </Container>
  );
};

export default PainelAdminDashboard;
