import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import apiClient from '../../api/axiosConfig';
import { Container, Row, Col, Card, Spinner, Button, ButtonGroup } from 'react-bootstrap';

const ListaLojas = () => {
  const [lojas, setLojas] = useState([]);
  const [loading, setLoading] = useState(true);
  // 1. Adicionar estado para controlar o modo de visualização
  const [viewMode, setViewMode] = useState('grid'); // 'grid' é o padrão

  useEffect(() => {
    apiClient.get('/lojas')
      .then(response => setLojas(response.data))
      .catch(error => console.error("Erro ao buscar lojas:", error))
      .finally(() => setLoading(false));
  }, []);

  if (loading) {
    return <Container className="text-center p-5"><Spinner animation="border" variant="primary" /></Container>;
  }

  return (
    <Container>
      {/* --- INÍCIO DA ALTERAÇÃO --- */}
      <div className="d-flex justify-content-between align-items-center mb-4 flex-wrap">
        <h1 className="mb-2 mb-md-0">Lojas Parceiras</h1>
        {/* 2. Adicionar os botões para alternar a visualização */}
        <ButtonGroup>
          <Button variant={viewMode === 'grid' ? 'primary' : 'outline-primary'} onClick={() => setViewMode('grid')}>
            <i className="bi bi-grid-3x3-gap-fill"></i> Cards
          </Button>
          <Button variant={viewMode === 'list' ? 'primary' : 'outline-primary'} onClick={() => setViewMode('list')}>
            <i className="bi bi-list-ul"></i> Lista
          </Button>
        </ButtonGroup>
      </div>

      {/* 3. Renderização condicional baseada no viewMode */}
      <Row>
        {lojas.map(loja => (
          // Se for grid, ocupa colunas menores. Se for lista, ocupa a largura toda.
          <Col key={loja._id} lg={viewMode === 'grid' ? 3 : 12} md={viewMode === 'grid' ? 4 : 12} className="mb-4">
            {/* O card da loja agora precisa de se adaptar à visualização em lista */}
            <Card as={Link} to={`/loja/${loja._id}`} className={`h-100 text-decoration-none text-dark card-hover border-0 shadow-sm ${viewMode === 'list' ? 'store-list-item' : ''}`}>
              <Card.Body className="d-flex align-items-center">
                <div className="store-logo-container">
                  <Card.Img src={loja.logomarcaUrl} className="store-logo" />
                </div>
                <div className="store-info">
                  <Card.Title className="m-0">{loja.nome}</Card.Title>
                </div>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      {/* --- FIM DA ALTERAÇÃO --- */}
    </Container>
  );
};

export default ListaLojas;
