import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

// Importe o componente de Rota Protegida que criaremos a seguir
import RotaProtegida from './components/auth/RotaProtegida';

// Importe as PÁGINAS que vamos criar
// Páginas Públicas (ainda não criadas)
// import PaginaInicial from './pages/PaginaInicial';
// import DetalhesVeiculo from './pages/DetalhesVeiculo';

// Páginas do Painel Administrativo
import PainelAdminLogin from './pages/admin/PainelAdminLogin';
import PainelAdminDashboard from './pages/admin/PainelAdminDashboard';
import FormularioVeiculo from './pages/admin/FormularioVeiculo';

function App() {
  return (
    <Router>
      <Routes>
        {/* --- ROTAS PÚBLICAS --- */}
        {/* Futuramente, aqui ficarão as rotas do site principal */}
        {/* Exemplo: <Route path="/" element={<PaginaInicial />} /> */}
        {/* Exemplo: <Route path="/veiculo/:id" element={<DetalhesVeiculo />} /> */}


        {/* --- ROTAS DO PAINEL ADMINISTRATIVO --- */}

        {/* A página de Login é pública dentro do escopo do admin */}
        <Route path="/admin/login" element={<PainelAdminLogin />} />

        {/* As rotas dentro de "RotaProtegida" só serão acessíveis
            se o utilizador estiver autenticado (com token válido) */}
        <Route element={<RotaProtegida />}>
          <Route path="/admin/dashboard" element={<PainelAdminDashboard />} />
          <Route path="/admin/veiculo/novo" element={<FormularioVeiculo />} />
          <Route path="/admin/veiculo/editar/:id" element={<FormularioVeiculo />} />
        </Route>

      </Routes>
    </Router>
  );
}

export default App;
