import React from 'react';
import { Nav } from 'react-bootstrap';
// Importamos o Link diretamente do react-router-dom
import { Link, useLocation } from 'react-router-dom';
import './SidebarAdmin.css';

const SidebarAdmin = ({ onLogout }) => {
  const location = useLocation();

  return (
    <div className="sidebar-wrapper">
      <Nav className="flex-column">
        <div className="sidebar-header">
          Painel Admin
        </div>
        
        {/* Usamos as={Link} em vez de LinkContainer */}
        <Nav.Link as={Link} to="/admin/dashboard" className={`sidebar-link ${location.pathname.includes('/dashboard') ? 'active' : ''}`}>
            <i className="bi bi-speedometer2 me-2"></i>Dashboard
        </Nav.Link>
        <Nav.Link as={Link} to="/admin/lojas" className={`sidebar-link ${location.pathname.includes('/loja') ? 'active' : ''}`}>
            <i className="bi bi-shop me-2"></i>Lojas
        </Nav.Link>
        
        {/* O link para "Ver Site" pode ficar no rodapé da sidebar */}
        <Nav.Link as={Link} to="/" className="sidebar-link mt-auto">
            <i className="bi bi-box-arrow-left me-2"></i>Ver Site Público
        </Nav.Link>
      </Nav>
    </div>
  );
};

export default SidebarAdmin;
